import { useState } from "react"
import MusiquesConst from "./Const/musiques"

function Musiques () {
    const [musiques,setMusique]= useState(MusiquesConst)
    const [ActiveMusique,SetActiveMusique] = useState(musiques[0])

    document.addEventListener('DOMContentLoaded', function() {
        new GreenAudioPlayer('.ready-player-1', { showTooltips: true, showDownloadButton: false, enableKeystrokes: true });
    });

    const Musiques =
    <div>
        <div className="container text-center">
            <div className="row">
                <div class="card col-lg-12 ">
                    
                    <div className="card-body">
                        <h5 className="card-title">{ActiveMusique.titre}</h5>
                        <div className="gap-example">
                            <audio>
                                <source src={ActiveMusique.url} type="audio/mp3"/>
                            </audio>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                {
                    musiques.map((e,i)=>
                    <div class="card col-lg-4">
                        <div class="card-body">
                            <h5 class="card-title">{e.titre}</h5>
                            <button class="btn btn-primary" onClick={()=>SetActiveMusique(e)}>Charger la musique</button>
                        </div>
                    </div>
                    )
                }
            </div>
        </div>
    </div>

    return(Musiques)
}

export default Musiques
