import { useEffect, useState } from "react"

function Programation(){
    const images = [
        "images/screen2.PNG",
        "images/screen3.PNG",
        "images/screen4.PNG",
        "images/screen5.PNG",
    ]

    const[indice,setImage]=useState(1)

    useEffect(()=>{
        const i = setInterval(()=>{
            var j = indice
            var nextImage = j++
    
            if(nextImage>images.length){
                nextImage = 0
            }

            console.log(indice)
    
            setImage(nextImage)
        },3000)
    },[])


    const prog =
    <div>
        <div className="container-fluid py-4">
            <div className="row">
                <div className="col-lg-6 my-auto">
                    <h2> Developpement web </h2>
                    <p> Ce site à principalement été réalisé avec react. Vous retrouverez le code source de ce dernier en suivant ce lien</p>
                    <p> Par ailleurs, voici le lien d'une autre de mes réalisations : un site web pour une pharmacie. Ce dernier à été réalisé avec vueJs pour la partie front-end et avec nodeJs pour la partie back-end.</p>
                </div>
                <div className="col-lg-6">
                    <img className="img-fluid" src={images[indice]} alt="First slide"/>
                </div>
            </div>
        </div>
    </div>

    return(prog)
}

export default Programation
