import Musiques from "../Const/musiques"
import React, { useEffect, useState } from 'react';
import "../../App.css"
import { getValue } from "@testing-library/user-event/dist/utils";

function MusicPlayer(){
    const[musiques,setMusique] = useState(Musiques)
    const[ActiveMusic,setActive]= useState(musiques[0])
    const[AudioMusic,setAudio]=useState(new Audio (Musiques[0].url))
    const[Duration,setDuration]=useState(0)

    

    function setActiveAudio(song){
        Pause()
        setActive(song)
        setAudio(new Audio(song.url))
        setDuration(AudioMusic.duration)
    }

    

    function Play(){
        AudioMusic.play()
        console.log(Duration)

    }

    function Pause(){
        AudioMusic.pause()
    }

    function SetTime(time){
        AudioMusic.currentTime=time/1000
    }


    return(
        
        <div className="text-center">
            <div className="container">
                <div className="row">
                <div className="col-lg-6 my-auto">
                    <div className="border border-3 rounded-4 p-4 player">
                        <div>
                            <h2>{ActiveMusic.titre}</h2>
                        </div>
                        <div className="py-4">
                            <h3>00:00/{Duration}</h3>
                        </div>
                        <div class="slidecontainer">
                            <input type="range" min="0" max={Duration*1000} defaultValue="0" class="slider" id="myRange" onChange={(e)=>{SetTime(e.target.value)}}/>
                        </div>
                        <div>
                            <svg xmlns="http://www.w3.org/2000/svg" width="80" height="80" fill="currentColor" class="bi bi-caret-left" viewBox="0 0 16 16">
                                <path d="M10 12.796V3.204L4.519 8 10 12.796zm-.659.753-5.48-4.796a1 1 0 0 1 0-1.506l5.48-4.796A1 1 0 0 1 11 3.204v9.592a1 1 0 0 1-1.659.753z"/>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="100" height="100" fill="currentColor" class="bi bi-play-fill" viewBox="0 0 16 16" onClick={()=>{Play()}}>
                                <path d="m11.596 8.697-6.363 3.692c-.54.313-1.233-.066-1.233-.697V4.308c0-.63.692-1.01 1.233-.696l6.363 3.692a.802.802 0 0 1 0 1.393z"/>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="80" height="80" fill="currentColor" class="bi bi-caret-right" viewBox="0 0 16 16">
                                <path d="M6 12.796V3.204L11.481 8 6 12.796zm.659.753 5.48-4.796a1 1 0 0 0 0-1.506L6.66 2.451C6.011 1.885 5 2.345 5 3.204v9.592a1 1 0 0 0 1.659.753z"/>
                            </svg>
                        </div>
                    </div>
                </div>
                <div className="col-lg-6">
                    <div>
                        {
                            musiques.map((e,i)=>
                                <div className="border border 2 py-2 my-2" onClick={()=>setActiveAudio(e)}>
                                    <h3>{e.titre}</h3>
                                </div>
                            )
                        }
                    </div>
                </div>
            </div>
            </div>
        </div>
    )
}

export default MusicPlayer
